    public static boolean validate(String email) throws ErrorRegistrationException {
        regexValidate(email);
        checkExistInDB(email);
        return true;
    }

    private static void regexValidate(String email) throws ErrorRegistrationException {
        Matcher matcher = VALID_EMAIL_ADDRESS_REGEX .matcher(email);
        if(!matcher.find()) {
            throw new ErrorRegistrationException(RegistrationErrors.EMAIL_IS_INCORRECT);
        }
    }

    private static void checkExistInDB(String email) throws ErrorRegistrationException {
        UserService userService = new UserService();
        User user = null;
        try {
            user = userService.getUserByEmail(email);
        } catch(EmptyResultDataAccessException e) { }
        if(user != null) {
            throw new ErrorRegistrationException(RegistrationErrors.EMAIL_EXIST);
        }
    }

